import React from "react";

class Income extends React.Component {
  state = { income: "" };
  enterIncome = (event) => {
    this.setState({ income: event.target.value });
  };

  submitIncome = (event) => {
    event.preventDefault();
    let x = document.forms["incomeForm"]["income"].value;
    if (x === "" || x === "null") {
      return false;
    }
    this.props.updateIncome(this.state.income);
  };
  render() {
    return (
      <div className="card" style={{ margin: 10 }}>
        <form name="incomeForm" onSubmit={this.submitIncome}>
          <div>
            <div>
              <label style={{ margin: 10 }}>Income</label>
            </div>
            <div className="ui input focus" style={{ margin: 10 }}>
              <input
                name="income"
                value={this.state.income}
                onChange={this.enterIncome}
                type="text"
                placeholder="Amount"
              />
            </div>
            <div>
              <button
                className="ui basic green button"
                type="submit"
                style={{ margin: 10 }}
              >
                Enter
              </button>
            </div>
          </div>
        </form>
      </div>
    );
  }
}

export default Income;
