import React from "react";

class ExpenseList extends React.Component {
  // state = { changedList: [...this.props.expenseList] };
  onDelete = (index) => {
    const newList = [...this.props.expenseList];
    newList.splice(index, 1);
    //   this.setState({ changedList: newList });
    console.log(newList);
    this.props.removeExpenseType(this.state.changedList);
  };
  render() {
    return (
      <div>
        <ul style={{ listStyleType: "none" }}>
          {this.props.expenseList.map((expense, index) => (
            <li key={index}>
              {expense}{" "}
              <button onClick={(e) => this.onDelete(index)}>
                <i className="trash alternate icon" />
              </button>
            </li>
          ))}
        </ul>
      </div>
    );
  }
}

export default ExpenseList;
