import React from "react";

const ExpenseList = ({ expenseList, expenseValue }) => {
  const onDelete = (index) => {
    const newList = expenseList.filter((expense, i) => i !== index);
    this.setState({ expenseList: newList });
    console.log(expenseList);
  };

  return (
    <div>
      <ul style={{ listStyleType: "none" }}>
        {expenseList.map((expense, index) => (
          <li key={index}>
            {expense}{" "}
            <i className="trash alternate icon" onClick={onDelete(index)} />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ExpenseList;
