import React from "react";

class Display extends React.Component {
  render() {
    return (
      <div class="ui statistics">
        <div class="statistic">
          <div class="value">22</div>
          <div class="label">Income</div>
        </div>
        <div>Expenses</div>
        <div>Balance</div>
      </div>
    );
  }
}

export default Display;
