import React from "react";

class App extends React.Component {
  state = { income: "", expense: "", expenseType: "" };

  enterIncome = (event) => {
    this.setState({ income: event.target.value });
  };

  enterExpense = (event) => {
    this.setState({ expense: event.target.value });
  };
  enterExpenseType = (event) => {
    this.setState({ expenseType: event.target.value });
  };

  submitIncome = (event) => {
    event.preventDefault();
    console.log(this.state.income);
  };

  submitExpense = (event) => {
    event.preventDefault();
    console.log(this.state.expense);
  };

  submitExpenseType = (event) => {
    event.preventDefault();
    console.log(this.state.expenseType);
  };
  render() {
    return (
      <div className="ui cards" style={{ margin: 10 }}>
        <div className="card" style={{ margin: 10 }}>
          <form onSubmit={this.submitIncome}>
            <div>
              <div>
                <label style={{ margin: 10 }}>Income</label>
              </div>
              <div className="ui input focus" style={{ margin: 10 }}>
                <input
                  value={this.state.income}
                  onChange={this.enterIncome}
                  type="text"
                  placeholder="Amount"
                />
              </div>
              <div>
                <button
                  className="ui basic green button"
                  type="submit"
                  style={{ margin: 10 }}
                >
                  Enter
                </button>
              </div>
            </div>
          </form>
        </div>

        <div className="card" style={{ margin: 10 }}>
          <form onSubmit={this.submitExpenseType}>
            <div>
              <div>
                <label style={{ margin: 10 }}>Expenses</label>
              </div>
              <div className="ui input focus" style={{ margin: 10 }}>
                <input
                  value={this.state.expenseType}
                  onChange={this.enterExpenseType}
                  type="text"
                  placeholder="Type of expenses"
                />
              </div>
              <div className="ui input focus" style={{ margin: 10 }}>
                <input
                  value={this.state.expense}
                  onChange={this.enterExpense}
                  onSubmit={this.submitexpense}
                  type="text"
                  placeholder="Amount"
                />
              </div>
              <div>
                <button
                  className="ui basic red button"
                  type="submit"
                  style={{ margin: 10 }}
                >
                  Enter
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default App;
