import React from "react";

class ExpenseList extends React.Component {
  onDelete = (index) => {
    const newList = [...this.props.expenseTypeList];
    newList.splice(index, 1);
    this.props.removeExpenseType(newList);
    const newSumList = [...this.props.myExpenseSumList];
    newSumList.splice(index, 1);
    const expenseSum = (arr) => arr.reduce((a, b) => a + b, 0);
    console.log(expenseSum);
    this.props.removeExpenseType(expenseSum);
  };
  render() {
    return (
      <div>
        <ul style={{ listStyleType: "none" }}>
          {this.props.expenseTypeList.map((expense, index) => (
            <li key={index}>
              {expense}{" "}
              <i
                onClick={(e) => this.onDelete(index)}
                className="trash alternate icon"
              />
            </li>
          ))}
        </ul>
      </div>
    );
  }
}

export default ExpenseList;
