import React from "react";
import Display from "./Display";
import Income from "./Income";
import Expense from "./Expense";

class App extends React.Component {
  updateIncome = (income) => {
    this.setState({ income: income });
    console.log(this.state.income);
  };

  updateExpense = (expense) => {
    this.setState({ expense: expense });
    console.log(this.state.expense);
  };

  updateExpenseType = (expenseType) => {
    this.setState({ expenseType: expenseType });
    console.log(this.state.expenseType);
  };

  render() {
    return (
      <div>
        <div className="ui cards" style={{ margin: 10 }}>
          <Income updateIncome={this.updateIncome} />
          <Display />
        </div>
        <div className="ui cards" style={{ margin: 10 }}>
          <Expense
            updateExpense={this.updateExpense}
            updateExpenseType={this.updateExpenseType}
          />
        </div>
      </div>
    );
  }
}

export default App;
